import React, { useState } from 'react';
import {
  Modal,
  ModalContent,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Button,
  useDisclosure,
  Input,
} from '@nextui-org/react';
import Form from 'react-bootstrap/Form';
import { Select, SelectItem } from '@nextui-org/react';

function NewItem(props) {
  const [show, setShow] = useState(false);
  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});

  const { isOpen, onOpen, onOpenChange } = useDisclosure();

  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });

    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const priority = [
    { label: 'High', value: 'High' },
    { label: 'Medium', value: 'Medium' },
    { label: 'Low', value: 'Low' },
  ];

  const validateForm = () => {
    const { task, date, priority, category } = form;
    const newErrors = {};

    if (!task || task === '') newErrors.task = 'Please enter a task.';
    if (!date || date === '') newErrors.date = 'Please select a valid date.';
    if (!priority || priority === 'Select priority')
      newErrors.priority = 'Please select a priority.';
    if (!category || category === 'Select list name')
      newErrors.category = 'Please select a list name.';

    return newErrors;
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    const formErrors = validateForm();
    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors);
    } else {
      handleClose();
      const newTask = {
        task: form.task,
        description: form.description,
        due_date: form.date,
        priority: form.priority,
        task_category: form.category,
        completed: false,
      };

      props.insertTask(form.category, newTask);
    }
  };

  return (
    <div>
      <Button variant="dark" onPress={onOpen} className="me-2">
        Add New Item
      </Button>
      <Modal
        backdrop="opaque"
        size="xl"
        isOpen={isOpen}
        onOpenChange={onOpenChange}
        radius="2x1">
        <ModalContent>
          {(onClose) => (
            <>
              <ModalHeader>Add New Item To List</ModalHeader>
              <ModalBody>
                <Form noValidate onSubmit={handleSubmit}>
                  <Form.Group
                    className="w-full flex flex-col gap-4"
                    controlId="formTask">
                    <Input
                      autoFocus
                      type="text"
                      label={<b>{'Task'}</b>}
                      placeholder="Physics homework"
                      value={form.task}
                      onChange={(e) => setField('task', e.target.value)}
                      labelPlacement="outside"
                      isInvalid={!!errors.task}
                      errorMessage={errors.task}
                      isRequired
                    />

                    <Form.Control.Feedback type="invalid">
                      {errors.task}
                    </Form.Control.Feedback>
                  </Form.Group>
                  <Form.Group
                    className="w-full flex flex-col gap-4 mb-3"
                    controlId="formDescription">
                    <Input
                      type="textarea"
                      label={<b>{'Description'}</b>}
                      placeholder="Problems from chapters 10.2 and 10.3"
                      value={form.task}
                      onChange={(e) => setField('description', e.target.value)}
                      labelPlacement="outside"
                    />
                  </Form.Group>
                  <Form.Group
                    className="w-full flex flex-col gap-4"
                    controlId="formDueDate">
                    <Input
                      type="date"
                      label={<b>{'Due Date'}</b>}
                      placeholder="2023-10-28"
                      value={form.date}
                      onChange={(e) => setField('date', e.target.value)}
                      labelPlacement="outside"
                      isInvalid={!!errors.date}
                      errorMessage={errors.task}
                      isRequired
                    />

                    <Form.Control.Feedback type="invalid">
                      {errors.date}
                    </Form.Control.Feedback>
                  </Form.Group>
                  <Form.Group
                    className="w-1/2 flex flex-col gap-4"
                    controlId="formPriority">
                    <Select
                      label={<b>{'Priority'}</b>}
                      labelPlacement="outside"
                      placeholder="Select Priority">
                      {priority.map((priority) => (
                        <SelectItem key={priority.value} value={priority.value}>
                          {priority.label}
                        </SelectItem>
                      ))}
                    </Select>

                    <Form.Label>Priority</Form.Label>
                    <Form.Select
                      aria-label="Default select example"
                      value={form.priority}
                      onChange={(e) => setField('priority', e.target.value)}
                      placeholder="Select priority"
                      isInvalid={!!errors.priority}
                      required>
                      <option>Select priority</option>
                      <option key={'Low'} value="Low">
                        Low
                      </option>
                      <option key={'Medium'} value="Medium">
                        Medium
                      </option>
                      <option key={'High'} value="High">
                        High
                      </option>
                    </Form.Select>
                    <Form.Control.Feedback type="invalid">
                      {errors.priority}
                    </Form.Control.Feedback>
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="formPriority">
                    <Form.Label>List Name</Form.Label>
                    <Form.Select
                      aria-label="Default select example"
                      value={form.category}
                      onChange={(e) => setField('category', e.target.value)}
                      placeholder="Select list name"
                      isInvalid={!!errors.category}
                      required>
                      <option>Select list name</option>
                      {props.list.map((task) => (
                        <option key={`${task.name}`} value={task.name}>
                          {task.name}
                        </option>
                      ))}
                    </Form.Select>
                    <Form.Control.Feedback type="invalid">
                      {errors.category}
                    </Form.Control.Feedback>
                  </Form.Group>
                </Form>
              </ModalBody>
              <ModalFooter>
                <Button variant="secondary" onClick={handleClose}>
                  Close
                </Button>
                <Button variant="dark" type="submit">
                  Add Item
                </Button>
              </ModalFooter>
            </>
          )}
        </ModalContent>
      </Modal>
    </div>
  );
}

export default NewItem;
