import React, { useState } from 'react';
import Form from 'react-bootstrap/Form';
import NewItem from './NewItem';
import { Button } from '@nextui-org/react';
import {
  Table,
  TableHeader,
  TableBody,
  TableColumn,
  TableRow,
  TableCell,
} from '@nextui-org/react';

function ListTable(props) {
  const listItemStyle = {
    textDecoration: 'line-through',
    marginBottom: '8px',
    color: '#ff0000',
  };

  /*
  TO-DO: 
  All Tasks list should show all tasks
  selectedKeys should change the style of the row that it is on
  use column key and mapping to make more readable code
    style to look closer to Figma model
  */

  const [selectedKeys, setSelectedKeys] = React.useState(new Set([]));

  const isDict = (dict) => {
    return typeof dict === 'object' && !Array.isArray(dict);
  };

  const grabList = (list, val) => {
    for (let sub of list) {
      if (sub.name == val) {
        if (sub.items == undefined) {
          return 'undefined';
        } else {
          return sub.items;
        }
      }
    }
    return [];
  };

  const grabCompleted = (list, val) => {
    for (let sub of list) {
      if (sub.name == val) {
        if (sub.completed == undefined) {
          return 'undefined';
        } else {
          return sub.completed;
        }
      }
    }
    return [];
  };

  const handleSelect = (key) => {
    setSelectedKeys(key);
    console.log(key);

    if (key === 'all') {
      Array.from(selectedKeys).map((key) => {
        let arr = key.split('-', 2);
        let completed = grabCompleted(grabList(props.list, arr[0]), arr[1]);
        console.log(completed);
        props.setChecked(arr[0], arr[1], !completed);
      });
    } else if (key.currentKey !== undefined) {
      let arr = key.currentKey.split('-', 2);
      let completed = grabCompleted(grabList(props.list, arr[0]), arr[1]);
      console.log(completed);
      props.setChecked(arr[0], arr[1], !completed);
    }

    /*find the dictionary that has the name attribute = arr[1]
    then grab the items attribute and grab completed info*/

    // Array.from(key).map((key) => {
    //   const arr = key.split('-', 2);
    //   var completed;
    //   if (props.list.length > 1) {
    //     if (isDict(props.list)) {
    //       completed = props.list[arr[0]].items[arr[1]].completed;
    //     } else {
    //       props.list.map((list) => list[arr[0]])
    //       completed = props.list.items[arr[1]].completed;
    //     }
    //     props.setChecked(arr[0], arr[1], !completed);
    //   }

    // props.setChecked(item.name, val.name, !val.completed);
    // });
  };

  return (
    <div>
      <NewItem insertTask={props.insertTask} list={props.list} />
      <Table
        selectionMode="multiple"
        selectedKeys={selectedKeys}
        onSelectionChange={handleSelect}
        aria-label="Example static collection table">
        <TableHeader>
          <TableColumn>Task</TableColumn>
          <TableColumn>Description</TableColumn>
          <TableColumn>Due Date</TableColumn>
          <TableColumn>Priority</TableColumn>
          <TableColumn>List Name</TableColumn>
          <TableColumn></TableColumn>
        </TableHeader>
        <TableBody emptyContent={'No rows to display.'}>{[]}</TableBody>

        <TableBody key={'unique'}>
          {props.list.map((list) =>
            list.items.map((val) => (
              <TableRow
                // style={val.completed ? listItemStyle : null}
                key={`${list.name}-${val.name}`}
                data-selected={this.key in selectedKeys}>
                <TableCell>{val.name}</TableCell>
                <TableCell>{val.description}</TableCell>
                <TableCell>{val.due_date}</TableCell>
                <TableCell>{val.priority}</TableCell>
                <TableCell>{list.name}</TableCell>
                <TableCell>
                  <Button
                    color="danger"
                    onClick={() => props.deleteTask(list.name, val.name)}>
                    Delete
                  </Button>
                </TableCell>
              </TableRow>
            )),
          )}
        </TableBody>
      </Table>
      {props.list.length === 1 && (
        <Button onClick={() => props.deleteList(props.list[0].name)}>
          Delete List
        </Button>
      )}
    </div>
  );
}
