// import Table from 'react-bootstrap/Table';
import Form from 'react-bootstrap/Form';
import NewItem from './NewItem';
import { Button } from '@nextui-org/react';
import {
  Table,
  TableHeader,
  TableBody,
  TableColumn,
  TableRow,
  TableCell,
} from '@nextui-org/react';

function ListTable(props) {
  const listItemStyle = {
    textDecoration: 'line-through',
    marginBottom: '8px',
    color: '#ff0000',
  };

  //{{}}
  return (
    <div>
      <NewItem insertTask={props.insertTask} list={props.list} />
      <Table>
        <TableHeader>
          <TableColumn>Task</TableColumn>
          <TableColumn>Description</TableColumn>
          <TableColumn>Due Date</TableColumn>
          <TableColumn>Priority</TableColumn>
          <TableColumn>List Name</TableColumn>
        </TableHeader>

        <TableBody items={props.list}>
          {(val) =>
            // Assuming items is an array, map over it to render rows
            val.items.map((item) => (
              <TableRow key={`${props.list._id}_${item.id}`}>
                <TableCell>{val.items[0].name}</TableCell>
                <TableCell>{item.description}</TableCell>
                <TableCell>{item.due_date}</TableCell>
                <TableCell>{item.priority}</TableCell>
                <TableCell>{props.list.name}</TableCell>
              </TableRow>
            ))
          }
        </TableBody>
      </Table>
    </div>
  );
}
