import React, { useState } from 'react';
import Form from 'react-bootstrap/Form';
import NewItem from './NewItem';
import { Button } from '@nextui-org/react';
import {
  Table,
  TableHeader,
  TableBody,
  TableColumn,
  TableRow,
  TableCell,
} from '@nextui-org/react';

function ListTable(props) {
  const listItemStyle = {
    textDecoration: 'line-through',
    marginBottom: '8px',
    color: '#ff0000',
  };

  const [selectedKeys, setSelectedKeys] = React.useState(new Set([]));

  const handleSelect = (event) => {
    setSelectedKeys();
  };

  return (
    <div>
      <NewItem insertTask={props.insertTask} list={props.list} />
      <Table
        selectionMode="multiple"
        selectedKeys={selectedKeys}
        onSelectionChange={handleSelect}
        aria-label="Example static collection table">
        <TableHeader>
          <TableColumn>Task</TableColumn>
          <TableColumn>Description</TableColumn>
          <TableColumn>Due Date</TableColumn>
          <TableColumn>Priority</TableColumn>
          <TableColumn>List Name</TableColumn>
        </TableHeader>
        <TableBody emptyContent={'No rows to display.'}>{[]}</TableBody>
        {props.list.map((list) => (
          <TableBody items={list.items}>
            {(val) => (
              <TableRow
                style={val.completed ? listItemStyle : text - bold}
                data-selected={val.completed}
                key={`${list._id}-${val._id}`}>
                <TableCell>{val.name}</TableCell>
                <TableCell>{val.description}</TableCell>
                <TableCell>{val.due_date}</TableCell>
                <TableCell>{val.priority}</TableCell>
                <TableCell>{list.name}</TableCell>
              </TableRow>
            )}
          </TableBody>
        ))}
      </Table>
    </div>
  );
}
